(function() {
  module.exports = {
    createModelCache: function(model, prop, key) {
      var _base;
      if (key == null) {
        key = '__getter_cache';
      }
      if (model[key] == null) {
        model[key] = {};
      }
      if ((_base = model[key])[prop] == null) {
        _base[prop] = {};
      }
      return model[key][prop];
    }
  };

}).call(this);
